include 'components/connect.php';

session_start();

if(isset($_SESSION['user_id'])){
   $user_id = $_SESSION['user_id'];
}else{
   $user_id = '';
};

if(isset($_POST['submit'])){

    $fname = $_POST['firstname'];
    $fname = filter_var($fname, FILTER_SANITIZE_STRING);
    $lname = $_POST['lastname'];
    $lname = filter_var($lname, FILTER_SANITIZE_STRING);
    $email = $_POST['email'];
    $email = filter_var($email, FILTER_SANITIZE_STRING);
    $phone = $_POST['phone'];
    $phone = filter_var($phone, FILTER_SANITIZE_STRING);
    $position = $_POST['position'];
    $position = filter_var($position, FILTER_SANITIZE_STRING);
    $startdate = $_POST['startdate'];
    $startdate = filter_var($startdate, FILTER_SANITIZE_STRING);

    $image = $_FILES['file']['name'];
   $image = filter_var($image, FILTER_SANITIZE_STRING);
   $image_size = $_FILES['file']['size'];
   $image_tmp_name = $_FILES['file']['tmp_name'];
   $image_folder = 'applicants/'.$image;
 
    $select_message = $conn->prepare("SELECT * FROM `applicants` WHERE firstname = ?  AND lastname = ? AND email = ? AND phone = ? AND position = ?  AND startdate = ? AND img = ?");
    $select_message->execute([$fname, $lname, $email, $phone,  $position, $startdate,$image]);
 
    if($select_message->rowCount() > 0){
       $message[] = 'already sent!';
      }else{
        if($image_size > 20000000){
           $message[] = 'image size is too large';
        }else{
           move_uploaded_file($image_tmp_name, $image_folder);
 
       $insert_message = $conn->prepare("INSERT INTO `applicants`(applicant_id, firstname, lastname, email, phone, position,startdate,img) VALUES(?,?,?,?,?,?,?,?)");
       $insert_message->execute(["",$fname, $lname, $email, $phone,  $position, $startdate,$image]);
 
       $message[] = 'sent message successfully!';
 
    }
 
 }
 
}

